import {Component, OnInit} from '@angular/core';
import {City} from '../../models/city';
import {Bank} from '../../models/bank';
import {Expense} from '../../models/expense';
import {Card} from '../../models/card';
import {NgForm} from '@angular/forms';

@Component({
  selector: 'app-information',
  templateUrl: './information.component.html',
  styleUrls: ['./information.component.css']
})
export class InformationComponent implements OnInit {
  filterCity;
  filterBank;
  filterExpense;
  filterCard;
  cities: City[];
  banks: Bank[];
  cards: Card[];
  idForm = true;
  expenses: Expense[];

  formAction = 'Add';
  formName = '';
  name; accountNo; sale; id; item; cost;

  constructor() {
  }

  ngOnInit(): void {
    this.cities = [
      {name: 'Gampaha', id: '1231563'},
      {name: 'Colombo', id: '1231563'}
    ];
    this.expenses = [
      {name: 'Fuel', id: '1231563'},
      {name: 'Food', id: '2458676'}
    ];
    this.banks = [
      {accountNo: '87534186468', name: 'Commercial Bank', id: '1231563'},
      {accountNo: '65595965968', name: 'NTB', id: '1231563'}
    ];

    this.cards = [
      {name: 'Kit Card', id: '1231563', cost: '47', item: '50', sale: '48'},
      {name: 'Kit Card', id: '2458676', cost: '900', item: '1000', sale: '930'}
    ];
  }

  editCity(city) {
    this.name = city.name;
    this.id = city.id;
  }

  deleteCity(city) {
  }

  editBank(bank) {
    this.name = bank.name;
    this.id = bank.id;
    this.accountNo = bank.accountNo;
  }

  deleteBank(bank) {
  }

  editCard(card) {
    this.name = card.name;
    this.id = card.id;
    this.item = card.item;
    this.cost = card.cost;
    this.sale = card.sale;
  }

  deleteCard(card) {
  }

  editExpense(expense) {
    this.id = expense.id;
    this.name = expense.name;
  }

  deleteExpense(expense) {
  }

  add(form: NgForm) {
    if (this.formAction === 'Add') {
      if (this.formName === 'City') {
        const city: City = {} as City;
        city.name = form.value.name;
        this.cities.push(city);
        console.log('Add city', city);
      }
      if (this.formName === 'Expense') {
        const expense: Expense = {} as Expense;
        expense.name = form.value.name;
        this.expenses.push(expense);
        console.log('Add Expense', expense);

      }
      if (this.formName === 'Bank') {
        const bank: Bank = {} as Bank;
        bank.name = form.value.name;
        bank.accountNo = form.value.accountNo;
        this.banks.push(bank);
        console.log('Add Bank', bank);

      }
      if (this.formName === 'Card') {
        const card: Card = {} as Card;
        card.name = form.value.name;
        card.sale = form.value.sale;
        card.cost = form.value.cost;
        card.item = form.value.item;
        this.cards.push(card);
        console.log('Add Card', card);
      }
    } else {
      if (this.formName === 'City') {
        const city: City = {} as City;
        city.name = form.value.name;
        city.id = form.value.id;
        console.log('Edit city', city);
      }
      if (this.formName === 'Expense') {
        const expense: Expense = {} as Expense;
        expense.name = form.value.name;
        expense.id = form.value.id;
        console.log('Edit Expense', expense);

      }
      if (this.formName === 'Bank') {
        const bank: Bank = {} as Bank;
        bank.id = form.value.id;
        bank.name = form.value.name;
        bank.accountNo = form.value.accountNo;
        console.log('Edit Bank', bank);

      }
      if (this.formName === 'Card') {
        const card: Card = {} as Card;
        card.name = form.value.name;
        card.id = form.value.id;
        card.sale = form.value.sale;
        card.cost = form.value.cost;
        card.item = form.value.item;
        console.log('Edit Card', card);
      }
    }
    form.resetForm();
    setTimeout(() => document.getElementById('modalToggle').click(), 500);

  }

}
<div class="row">
<!--  City List-->
  <div class="col-md-3">
    <div class="container">
      <div class="box-driver">
        <div class="form-row">
          <div class="col-md-3 col-6">
            <button data-toggle="modal" data-target="#addmodal" (click)="formAction = 'Add';formName = 'City'"
                    type="button" class="btn-sm btn-secondary">Add City
            </button>
          </div>
          <div class="col-md-4 col-6">
            <h5>Cities</h5>
          </div>
          <br><br>
          <input type="text" class="form-control col-md-3 col-sm-1" placeholder="Search"
                 [(ngModel)]="filterCity">
        </div>
        <br>
        <div class="list-group  driver-list ">
          <div *ngFor="let city of cities | filter:'name' :filterCity; let i = index;">
            <div class="form-row list-group-item list-group-item-action">
              <a class="name-action col-md-3 ">{{city.name}}</a>
              <span class="col-md-3">
            <a data-toggle="modal" data-target="#addmodal" class="actions-edit actions"
               (click)="editCity(city);formName = 'City';formAction = 'Edit'">Edit </a>
            <a class="actions" (click)="deleteCity(city)"> Delete</a>
          </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

<!--  Bank List-->
  <div class="col-md-3">
    <div class="container">
      <div class="box-driver">
        <div class="form-row">
          <div class="col-md-3 col-6">
            <button data-toggle="modal" data-target="#addmodal" (click)="formAction = 'Add';formName = 'Bank'"
                    type="button" class="btn-sm btn-secondary">Add Bank
            </button>
          </div>
          <div class="col-md-4 col-6">
            <h5>Banks</h5>
          </div>
          <br><br>
          <input type="text" class="form-control col-md-3 col-sm-1" placeholder="Search"
                 [(ngModel)]="filterBank">
        </div>
        <br>
        <div class="list-group  driver-list ">
          <div *ngFor="let bank of banks | filter:'name' :filterBank; let i = index;">
            <div class="form-row list-group-item list-group-item-action">
              <a class="name-action col-md-3 ">{{bank.name}}</a>
              <span class="col-md-3">
            <a data-toggle="modal" data-target="#addmodal" class="actions-edit actions"
               (click)="editBank(bank);formName = 'Bank';formAction = 'Edit'">Edit </a>
            <a class="actions" (click)="deleteBank(bank)"> Delete</a>
          </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

<!--  Expense List-->
  <div class="col-md-3">
    <div class="container">
      <div class="box-driver">
        <div class="form-row">
          <div class="col-md-3 col-6">
            <button data-toggle="modal" data-target="#addmodal" (click)="formAction = 'Add';formName = 'Expense'"
                    type="button" class="btn-sm btn-secondary">Add Expense
            </button>
          </div>
          <div class="col-md-4 col-6">
            <h5>Expenses</h5>
          </div>
          <br><br>
          <input type="text" class="form-control col-md-3 col-sm-1" placeholder="Search"
                 [(ngModel)]="filterExpense">
        </div>
        <br>
        <div class="list-group  driver-list ">
          <div *ngFor="let expense of expenses | filter:'name' :filterExpense; let i = index;">
            <div class="form-row list-group-item list-group-item-action">
              <a class="name-action col-md-3 ">{{expense.name}}</a>
              <span class="col-md-3">
            <a data-toggle="modal" data-target="#addmodal" class="actions-edit actions"
               (click)="editExpense(expense);formName = 'Expense';formAction = 'Edit'">Edit </a>
            <a class="actions" (click)="deleteExpense(expense)"> Delete</a>
          </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

<!--  Card List-->
  <div class="col-md-3">
    <div class="container">
      <div class="box-driver">
        <div class="form-row">
          <div class="col-md-3 col-6">
            <button data-toggle="modal" data-target="#addmodal" (click)="formAction = 'Add';formName = 'Card'"
                    type="button" class="btn-sm btn-secondary">Add Card
            </button>
          </div>
          <div class="col-md-4 col-6">
            <h5>Cards</h5>
          </div>
          <br><br>
          <input type="text" class="form-control col-md-3 col-sm-1" placeholder="Search"
                 [(ngModel)]="filterCard">
        </div>
        <br>
        <div class="list-group  driver-list ">
          <div *ngFor="let card of cards | filter:'name' :filterCard; let i = index;">
            <div class="form-row list-group-item list-group-item-action">
              <a class="name-action col-md-3 ">{{card.name}}</a>
              <span class="col-md-3">
            <a data-toggle="modal" data-target="#addmodal" class="actions-edit actions"
               (click)="editCard(card);formName = 'Card';formAction = 'Edit'">Edit </a>
            <a class="actions" (click)="deleteCard(card)"> Delete</a>
          </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>

<!-- Modal -->
<div class="modal fade" id="addmodal" tabindex="1" role="dialog" aria-hidden="true">
  <div class="modal-dialog modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">{{formAction}} {{formName}}</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <form #Form="ngForm" (ngSubmit)="add(Form)">
          <div class="form-group">
            <label>Name</label>
            <input type="text" required class="form-control" name="name" [(ngModel)]="name">
          </div>
          <div *ngIf="formName=='Bank'" class="form-group">
            <label>Account Number</label>
            <input type="text" required class="form-control" name="accountNo" [(ngModel)]="accountNo">
          </div>
          <div *ngIf="formName=='Card'" class="form-group">
            <label>Cost</label>
            <input type="number" required class="form-control" name="cost" [(ngModel)]="cost">
          </div>
          <div *ngIf="formName=='Card'" class="form-group">
            <label>Item</label>
            <input type="number" required class="form-control" name="item" [(ngModel)]="item">
          </div>
          <div *ngIf="formName=='Card'" class="form-group">
            <label>Sale</label>
            <input type="number" required class="form-control" name="sale" [(ngModel)]="sale">
          </div>
          <div hidden class="form-group">
            <label>Id</label>
            <input  class="form-control" name="id" [(ngModel)]="id">
          </div>


          <div class="modal-footer">
            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
            <button type="submit" [disabled]="Form.invalid || Form.pending"
                    class="btn btn-primary">Save changes
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>


<button type="button" id="modalToggle" hidden data-toggle="modal" data-target="#addmodal">
</button>
