import {Component, ElementRef, OnInit, ViewChild} from '@angular/core';
import {NgForm} from '@angular/forms';
import {EndStock} from '../../models/endStock';
import {DomSanitizer} from '@angular/platform-browser';
import jsPDF from 'jspdf';
import {debounceTime, distinctUntilChanged, filter, map} from 'rxjs/operators';
import {Observable, of, Subject, merge} from 'rxjs';
import {NgbTypeahead} from '@ng-bootstrap/ng-bootstrap';

interface Option {
  name: any;
  qty: any;
}

@Component({
  selector: 'app-driver',
  templateUrl: './driver.component.html',
  styleUrls: ['./driver.component.css']
})

export class DriverComponent implements OnInit {
  @ViewChild('pdfTable', {static: false}) pdfTable: ElementRef;

  pendingMessage = '';
  pendingMessageColour = '';
  link = '';
  imgBase64;
  PMTempFail = true;
  stockEnd: EndStock;
  userName;
  expenses: { expense: string, amount: number }[] = [];
  imgURL: any;
  bankList = [
    {name: 'Commercial Bank', amount: '', slipNo: ''},
    {name: 'NTB Bank', amount: '', slipNo: ''},
    {name: 'BOC Bank', amount: '', slipNo: ''},
  ];

  list = [
    {name: 'Int 99', qty: 122},
    {name: 'Int 49', qty: 54},
    {name: 'Reload 100', qty: 456},
    {name: 'Int 199', qty: 54},
    {name: 'Int 322', qty: 45},
  ];
  Rlist = [
    {name: 'Int 99', qty: 122},
    {name: 'Int 49', qty: 54},
    {name: 'Reload 100', qty: 456},
    {name: 'Int 199', qty: 54},
    {name: 'Int 322', qty: 55},
  ];

  expenseList = [
    'Staff Welfaire', 'Stationery', 'Account Fee', 'Office Maintain', 'Insuarance', 'Telephone', 'Postage',
    'Income Tax', 'Overdraft Interest', 'Elecricity -0506790306', 'Elecricity -0507162502',
    'Retailer Commission', 'Bank Charges', 'EPF', 'ETF', 'Travaling & Transport', 'Discount',
    'Donation', 'Fuel & Maintain', 'BGO 1019', 'BGO 1052', 'BGO 1043', 'BGO 1069', 'BGO 1024',
    'BGO 1065', 'BGO 1014', 'BGO 1056', 'BGO 1035', 'BDT 1090',
    'BDT 1098', 'BDT 1104', 'BAR 6748', 'CAW 4647', 'PJ 5178', 'KM 2229'];

  focus: Subject<string> = new Subject();
  click: Subject<string> = new Subject();

  // textStr: Subject<string> = new Subject();


  constructor(private domSanitizer: DomSanitizer) {
  }

  ngOnInit(): void {
    if (localStorage.getItem('userName') !== '') {
      this.userName = localStorage.getItem('userName');
    } else {
      this.userName = sessionStorage.getItem('userName');
    }
    if (this.PMTempFail) {
      this.pendingMessage = 'Your last Proposal was Rejected Due to the reason of Incorrect Slip number';
      this.pendingMessageColour = 'alert-danger';
      this.link = 'Click Here';
    } else {
      this.pendingMessage = 'Your last Proposal was Approved';
      this.pendingMessageColour = 'alert-success';
    }

    this.expenses.push({expense: '', amount: null});
  }

  // formatter = (option: { name: string }) => option.name;
  // search = (text: Observable<string>) =>
  //   merge(text, this.focus, this.click).pipe(
  //     map(term => term === '' ? this.expenseList
  //       : this.expenseList.filter(v => v.toLowerCase().indexOf(term.toLowerCase()) > -1))
  //   )

  // Type head Search Function
  search = (text: Observable<string>) => {
    const debouncedText$ = text.pipe(debounceTime(200), distinctUntilChanged());
    const inputFocus$ = this.focus;

    return merge(debouncedText$, inputFocus$).pipe(
      map(term => (term === '' ? this.expenseList
        : this.expenseList.filter(v => v.toLowerCase().indexOf(term.toLowerCase()) > -1)))
    );
  }

  // buy stock modal save and upload data
  buyStock() {
    console.log(this.list);
  }

  // summary modal save and upload data
  confirmSale() {
    console.log(this.stockEnd);
  }

  // Add new expense
  addNewExpense() {
    this.expenses.push({expense: '', amount: null});

  }

  // convert image to base64
  onFileChanges(event) {
    this.imgBase64 = (event[0].base64);
    this.imgURL = this.domSanitizer.bypassSecurityTrustResourceUrl(event[0].base64);
  }

  // validation in buy stock modal table
  valueChanged(e) {
    if (e.bought > e.qty) {
      e.bought = e.qty;
    }
  }

  // validation in end stock modal table
  valueChanged1(e) {
    if (e.remaining > e.qty) {
      e.remaining = e.qty;
    }
  }

  // stock end modal save
  stockEndSubmit(form: NgForm) {
    const dateFormat = require('dateformat');
    const now = new Date();
    const date = dateFormat(now, 'mmmm dS, yyyy, h:MM:ss TT');

    const endStock: EndStock = {} as EndStock;
    endStock.remainingStock = this.Rlist;
    endStock.banks = this.bankList;
    endStock.remainingCash = form.value.remainingCash;
    endStock.expenses = this.expenses;
    // endStock.bankSlipNo = form.value.slipNo;
    // endStock.bankSlipImg = this.imgBase64;
    endStock.notes = form.value.notes;
    endStock.date = date;
    this.stockEnd = endStock;

    // console.log(this.stockEnd, date);
    // console.log(this.stockEnd);
    // console.log(this.expenses);
    document.getElementById('btntoggle').click();
    // this.imgURL = this.domSanitizer.bypassSecurityTrustResourceUrl(endStock.bankSlipImg);
    setTimeout(() => document.getElementById('modalToggle').click(), 1000);

  }

  // public downloadAsPDF() {
  //   const doc = new jsPDF();
  //   const specialElementHandlers = {
  //     '#editor'(element, renderer) {
  //       return true;
  //     }
  //   };
  //   const pdfTable = this.pdfTable.nativeElement;
  //   doc.fromHTML(pdfTable.innerHTML, 15, 15, {
  //     width: 190, elementHandlers: specialElementHandlers
  //   });
  //   doc.save('tableToPdf.pdf');
  // }
}
<div class="alert {{pendingMessageColour}} alert-dismissible fade show" role="alert">
  {{pendingMessage}} to resubmit
  <a href="#" i18n="@@driverAlertClick" [hidden]="!PMTempFail" class="alert-link">Click Here</a>
  <button type="button" class="close" data-dismiss="alert" aria-label="Close">
    <span aria-hidden="true">&times;</span>
  </button>
</div>

<div class="container">
  <div class="box">
    <h4 i18n="@@driverCardTableName">Current Stock</h4>
    <div class="container-fluid">
      <table class="table table-bordered">
        <thead>
        <tr>
          <th i18n="@@driverCardNo" scope="col">Card Name</th>
          <th i18n="@@driverCardQty" scope="col">Quantity</th>
        </tr>
        </thead>
        <tbody>
        <tr *ngFor=" let option of list; let i=index">
          <th scope="row">{{option.name}}</th>
          <td>{{option.qty}}</td>
        </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>

<div class="btn-row container">
  <div class="row">
    <div class="col-md-6 ">
      <div class="container">
        <button i18n="@@driverNewStock" type="button" data-toggle="modal" data-target="#buyModal"
                class="btn btn-primary">Buy New Stock
        </button>
      </div>
    </div>
    <div class="col-md-6 ">
      <div class="container">
        <button i18n="@@driverEndStock" type="button" data-toggle="modal" data-target="#endModal"
                class="btn btn-danger">End Stock
        </button>
      </div>
    </div>
  </div>
</div>


<!-- Modal -->
<div class="modal fade" id="buyModal" tabindex="1" role="dialog" aria-hidden="true">
  <div class="modal-dialog modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" i18n="@@driverAddModalTitle">Select Quantity</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <table class="table table-bordered">
          <thead>
          <tr>
            <th i18n="@@driverCardNo" scope="col">Card Name</th>
            <th i18n="@@driverCardQty" scope="col">Quantity</th>
          </tr>
          </thead>
          <tbody>
          <tr *ngFor=" let option of list; let i=index">
            <th scope="row">{{option.name}}</th>
            <td><input [ngClass]="{btnRedClass: option.qty < count.value}" class="inputQty"
                       (change)="valueChanged(option)" type="number"
                       [(ngModel)]="option.bought" name="count" #count="ngModel" min="0" max="{{option.qty}}">
            </td>
          </tr>
          </tbody>
        </table>
      </div>
      <div class="modal-footer">
        <button type="button" i18n="@@driverModalClose" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" i18n="@@driverModalSave" (click)="buyStock()" data-dismiss="modal"
                class="btn btn-primary">Save changes
        </button>
      </div>
    </div>
  </div>
</div>


<div class="modal fade" id="endModal" data-dismiss="modal" tabindex="-1" role="dialog"
     aria-labelledby="exampleModalCenterTitle"
     aria-hidden="true">
  <div class="modal-dialog modal-dialog-centered" role="document">
    <div id="modalOneContent" class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" i18n="@@driverEndModalTitle">End Stock</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <form #endForm="ngForm" (ngSubmit)="stockEndSubmit(endForm)">
          <div class="form-group">
            <label i18n="@@driverModalRemainStock">Today's Stock</label>
            <table class="table table-bordered ">
              <thead>
              <tr>
                <th i18n="@@driverCardNo" scope="col">Card Name</th>
                <th i18n="@@driverCardQty" scope="col">Quantity</th>
              </tr>
              </thead>
              <tbody>
              <tr *ngFor=" let option of Rlist; let i=index">
                <th scope="row">{{option.name}}</th>
                <td><input [ngClass]="{btnRedClass: option.qty < count1.value}" class="inputQty"
                           (change)="valueChanged1(option)" type="number"
                           [(ngModel)]="option.remaining" required name="remcount_{{i}}" #count1="ngModel" min="0"
                           max="{{option.qty}}">
                </td>
              </tr>
              </tbody>
            </table>
          </div>
          <label i18n="@@driverModalBanked">Banked details</label>
          <div *ngFor="let option of bankList; let i = index" class="form-row bankList">
            <div class="form-group col-md-6 col-sm-12">
              <div class="form-check">
                <input class="form-check-input" ngModel type="checkbox" value="" #val="ngModel" name="bankName_{{i}}"
                       id="defaultCheck1_{{i}}">
                <label class="form-check-label" for="defaultCheck1_{{i}}">
                  {{option.name}}
                </label>
              </div>
            </div>
            <div class="form-group col-md-6 col-sm-12">
              <input type="number" class="form-control" placeholder="Banked Amount" [disabled]="!val.value"
                     [(ngModel)]="option.amount" required maxlength="5" name="bankAmount_{{i}}" ngModel>
            </div>
            <div class="form-group col-md-12 col-sm-12">
              <input type="number" class="form-control" placeholder="Banked Slip Number" [disabled]="!val.value"
                     [(ngModel)]="option.slipNo" required name="slip_{{i}}" ngModel>
            </div>
          </div>

          <div class="form-group">
            <label i18n="@@driverModalRemainCash">Balance Due</label>
            <input type="number" class="form-control" name="remainingCash" ngModel>
          </div>

          <div class="form-group">
            <label i18n="@@driverModalExpenses">Expenses</label>
            <div *ngFor="let expense of expenses; let i = index;">
              <div class="form-row">
                <div class="form-group col-md-7 col-sm-12">
                  <input id="typeahead-template" name="{{i}}_name" type="text" class="form-control"
                         [(ngModel)]="expense.expense" [ngbTypeahead]="search" placeholder="Select Expense"
                          (focus)="focus.next($any($event).target.value)"
                         (click)="click.next($any($event).target.value);" />
                </div>
                <div class="form-group col-md-5 col-sm-12">
                  <input placeholder="Enter Amount" class="form-control" name="{{i}}_value" required
                         [disabled]="!expense.expense" type="number" [(ngModel)]="expense.amount"/>
                </div>
              </div>


            </div>
            <button type="button" (click)="addNewExpense()" class="btn btn-secondary">Add Expense +</button>

            <!--            <input type="number" class="form-control" name="expenses" ngModel>-->
          </div>
