import { Component } from '@angular/core';
import {Router} from '@angular/router';
import {ValidateService} from './services/validate.service';
import {Role} from './models/role';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Reload';
  name = '';

  constructor(private router: Router, private authService: ValidateService) {
  }

  get isAuthorized() {
    if (localStorage.getItem('userName') !== '') {
        this.name = localStorage.getItem('userName');
    }else {
      this.name = sessionStorage.getItem('userName');
    }
    return this.authService.isAuthorized();
  }

  get isAdmin() {
    return this.authService.hasRole(Role.Admin);
  }
  get isDriver() {
    return this.authService.hasRole(Role.Driver);
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['login']);
  }
}
<html>
<body>

<header class="navbar navbar-expand navbar-dark bg-dark">
  <ul class="navbar-nav mr-auto">
    <!--    <li class="nav-item">-->
    <!--      <a class="nav-link" [routerLink]="['/']">Home</a>-->
    <!--    </li>-->

    <!-- Authorized only -->
    <li class="nav-item" *ngIf="isAdmin">
      <a class="nav-link" [routerLink]="['admin', 'panel']">Dashboard</a>
    </li>
    <li class="nav-item" *ngIf="isAdmin">
      <a class="nav-link" [routerLink]="['admin', 'addClients']">Drivers</a>
    </li>
    <!-- Admin only -->
    <!--    <li class="nav-item" *ngIf="isAdmin">-->
    <!--      <a class="nav-link" [routerLink]="['admin', 'control']">Admins Privileges</a>-->
    <!--    </li>-->
    <li class="nav-item" *ngIf="isAdmin">
      <a class="nav-link" [routerLink]="['admin', 'stocks']">Stocks</a>
    </li>

    <!-- Drivers only -->
    <li class="nav-item" *ngIf="isDriver || isAdmin">
      <a class="nav-link" [routerLink]="['driver']">Users</a>
    </li>

    <!-- Anonymous only -->
    <li class="nav-item" *ngIf="!isAuthorized">
      <a class="nav-link" [routerLink]="['login']">Login</a>
    </li>

    <!-- Authorized only -->
    <li class="nav-item" *ngIf="isAuthorized">
      <a i18n="@@navLogout" class="nav-link" (click)="logout()">Logout</a>
    </li>
  </ul>

  <span class="navbar-text">
      <span class="badge badge-danger" *ngIf="isAdmin">Admin</span>
      <span class="badge badge-warning" *ngIf="isAuthorized">{{name}}</span>
    </span>
</header>

<div class="container-fluid">
  <router-outlet></router-outlet>
</div>


</body>
<footer class="footer bg-dark fixed-bottom mt-auto py-3">
  <div class="container">
    <span class="text-muted">Place sticky footer content here.</span>
  </div>
</footer>
</html>
