import { Component, OnInit } from '@angular/core';
import {Subject} from 'rxjs';
import {NgbActiveModal, NgbModalRef} from '@ng-bootstrap/ng-bootstrap';
import {NgForm} from '@angular/forms';
import {Stock} from "../../../models/stock";

@Component({
  selector: 'app-modal-content',
  templateUrl: './modal-content.component.html',
  styleUrls: ['./modal-content.component.css']
})
export class ModalContentComponent implements OnInit {
  title = '';
  isUpdate = false;
  data: Stock;
  onSave = new Subject();

  constructor(private modalRef: NgbActiveModal) { }

  ngOnInit(): void {
    console.log(this.data);
  }

  close() {
    this.modalRef.close();
  }

  saveChanges(form: NgForm) {
    this.onSave.next(form.value);
  }

}
<div class="modal-content">
  <div class="modal-header">
    <h5 class="modal-title">Driver</h5>
    <button type="button" class="close" (click)="close()" aria-label="Close">
      <span aria-hidden="true">&times;</span>
    </button>
  </div>
  <div class="modal-body">
    <form #form="ngForm" (ngSubmit)="saveChanges(form)">
      <div class="form-group">
        <label>Driver Name</label>
        <input type="text" required class="form-control" name="driverName" ngModel>
      </div>
      <div class="form-group">
        <label>Driver NIC</label>
        <input type="text" required class="form-control" name="driverNic" ngModel>
      </div>
<!--      <div class="form-group">-->
<!--        <label>District</label>-->
<!--        <select required [(ngModel)]="driverCity" name="district" class="form-control">-->
<!--          <option *ngFor="let district of districts; let i = index">{{district}}</option>-->
<!--        </select>-->
<!--      </div>-->
      <div class="form-group">
        <label>Driver Email</label>
        <input type="email" class="form-control" required name="driverEmail" ngModel>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="submit" class="btn btn-primary">Save changes
        </button>
      </div>
    </form>
  </div>
</div>
